function () {
  const wordReplacements = Api1.data;
  let sentence = RichTextEditor1.text;
  for (const replacement of wordReplacements) {
    const oldWord = replacement.Old;
    const newWord = replacement.New;
    const regex = new RegExp("\\b" + oldWord + "\\b", "gi");
    sentence = sentence.replace(regex, `<mark>${newWord}</mark>`);
  }
  return sentence;
}

